import React, { useState } from "react";

export default function TodoForm() {
  const [taskDesc, setTaskDesc] = useState("");
  const [dueDate, setDueDate] = useState("");
  const [taskPriority, setTaskPriority] = useState("");
  const [taskCompleted, setTaskCompleted] = useState(false);

  function handleSubmit(e) {
    e.preventDefault();
    console.log(taskDesc, taskCompleted, taskPriority, dueDate);
  }

  return (
    <div className="text-center mt-10">
      <span className="text-4xl font-semibold">Todo Form</span>

      <form className="mt-8 mx-auto max-w-3xl p-6 bg-white shadow-md rounded-lg space-y-6">
        <div className="text-left">
          <label className="block text-lg font-medium mb-1">Task Desc:</label>
          <input
            required={true}
            value={taskDesc}
            className="w-full border rounded-md border-gray-400 px-3 py-2"
            type="text"
            onChange={(e) => setTaskDesc(e.target.value)}
          />
        </div>

        <div className="text-left">
          <label className="block text-lg font-medium mb-1">Due Date:</label>
          <input
            required={true}
            value={dueDate}
            className="w-full border rounded-md border-gray-400 px-3 py-2"
            type="date"
            onChange={(e) => setDueDate(e.target.value)}
          />
        </div>

        <div className="text-left">
          <label className="block text-lg font-medium mb-1">
            Task Priority:
          </label>
          <select
            required={true}
            value={taskPriority}
            onChange={(e) => setTaskPriority(e.target.value)}
            className="w-full border rounded-md border-gray-400 px-3 py-2"
          >
            <option value="" disabled>
              -- Select Priority --
            </option>
            <option value="Very Low">Very Low</option>
            <option value="Low">Low</option>
            <option value="Medium">Medium</option>
            <option value="High">High</option>
            <option value="Very High">Very High</option>
          </select>
        </div>

        <div className="flex items-center space-x-3 text-left">
          <input
            className="w-5 h-5 cursor-pointer border-gray-400"
            type="checkbox"
            value={taskCompleted}
            onChange={(e) => setTaskCompleted(e.target.checked)}
          />
          <label htmlFor="completed" className="text-lg">
            Task Completed?
          </label>
        </div>

        {/* Submit Buttons */}
        <div className="flex justify-center space-x-4 pt-4">
          <button
            onClick={handleSubmit}
            className="bg-gray-500 text-white px-4 py-2 rounded hover:bg-gray-600"
          >
            Add Todo
          </button>
          <button className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600">
            Confirm Update
          </button>
          <button className="bg-green-500 text-white px-4 py-2 rounded hover:bg-green-600">
            Clear Form
          </button>
        </div>
      </form>
    </div>
  );
}
